mapper.insert:
INSERT INTO book (
    book_tit, book_author, book_publi, book_publidate,
    book_genre, book_isbn, book_price, book_callsign, book_img
) VALUES (
    #{book_tit}, #{book_author}, #{book_publi}, #{book_publidate},
    #{book_genre}, #{book_isbn}, #{book_price}, #{book_callsign}, #{book_img}
)

mapper.update:
UPDATE book SET book_tit = #{book_tit}, book_author = #{book_author}, book_publi = #{book_publi}, book_publidate = #{book_publidate}, book_genre = #{book_genre}, book_isbn = #{book_isbn}, book_price = #{book_price}, book_callsign = #{book_callsign}
, book_img = #{book_img}
WHERE book_id = #{book_id}

mapper.select:

  SELECT A.*
	FROM (
	    SELECT rownum AS rnum, B.*
	    FROM (
	        SELECT *
	        FROM BOOK
	        WHERE 1=1

	            AND BOOK_TIT LIKE '%' || #{searchWord} || '%'

	        ORDER BY BOOK_PUBLIDATE DESC
	    ) B 
	    WHERE rownum <= #{end}
	) A	
	WHERE A.rnum >= #{start}
