mapper.select:

  SELECT A.*
	FROM (
	    SELECT rownum AS rnum, B.*
	    FROM (
	        SELECT *
	        FROM POSTS
	        WHERE 1=1

	            AND POST_TIT LIKE '%' || #{searchWord} || '%'

	        ORDER BY POST_DATE DESC
	    ) B 
	    WHERE rownum <= #{end}
	) A
	WHERE A.rnum >= #{start}
